INSERT INTO `book` VALUES ('92', '最强位面路人', '1258000', '北火', null, null, '//qidian.qpic.cn/qdbimg/349573/1004837715/150', null, '叶方发现自己要了不得了。他蹲坑蹲出来了一个能让他穿越无尽位面的路人系统。电影、动漫……甚至是小说里的世界。生化危机、美国队长……于是，叶方开始了拯（', '连载中', '5');
