INSERT INTO `book` VALUES ('208', '小农民修真', '259', '老树昏鸭', null, null, '//qidian.qpic.cn/qdbimg/349573/3688395/150', null, '【都市强力推荐，十万人同时在线阅读】厌烦都市生活，得龙之传承，回村种种田，放放牛，各种装B各种嗨，一切只为轻松。小农民人气群：131716192', '连载中', '4');
INSERT INTO `book` VALUES ('209', '我家食神是萝莉', '29', '贼猫小黑', null, null, '//qidian.qpic.cn/qdbimg/349573/1005719302/150', null, '“尊敬的美食之神啊，请你用神力庇护我们吧，我们愿奉上信仰！”“信仰？不，我只爱美食。我要吃遍整个星辰大海！”袁枚：“呵呵，没吃过中华美食的你们，根本不懂什么是美食！”', '连载中', '5');
