INSERT INTO `book` VALUES ('212', '东方神探九录', '42', '布衣廷尉', null, null, '//qidian.qpic.cn/qdbimg/349573/1006469018/150', null, '严正声明：本书不写神神鬼鬼，只写人性！本书翔实记录了发生在现代都市的九桩离奇谜案，每一件都出人意表，每一件又都在情理之中，在这些案件中，展现出复杂而多变的人性，扭曲而矛盾的心理，挣扎而', '连载中', '4');
